import React from "react";
import { View ,Text,TouchableOpacity,StyleSheet} from "react-native";

function CustomButton({text,onpress,type,color}) {
  return (
    <TouchableOpacity  onpress = {onpress} style = {[styles.container,styles[`container_${type}`]]}>
      <Text style = {[styles.text,styles[`container_${color}`]]}>{text}</Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({

  container:{

    width:"100%",
    height:30,
    alignItems:"center",
    justifyContent:"center",
    borderRadius:10,
    marginBottom:10,
    padding:10,

  },
  text : {
    fontWeight :"bold",
    color:"white"

  },

  text_PRIMARY : {
    color:"white"

  },

  text_SECONDARY : {
    color:"white"

  },
  container_PRIMARY : {
    backgroundColor:"#102740",
  }
  ,
  container_SECONDARY : {
    backgroundColor:"black",
  }
})

export default CustomButton;
