import React from "react";
import { View ,Text,TouchableOpacity,StyleSheet} from "react-native";

function CustomButton({text,onpress,type}) {
  return (
    <TouchableOpacity  onpress = {onpress} style = {[styles.container,{backgroundColor:styles[`container_${type}`]}]}>
      <Text>{text}</Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({

  container:{

    width:"100%",
    height:30,
    alignItems:"center",
    justifyContent:"center",
    borderRadius:10
  },
  text : {
    fontWeight :"bold",
    color:"white"

  },
  container_PRIMARY : {
    backgroundColor:"#102740",
  }
  ,
  container_SECONDARY : {
    backgroundColor:"##4a4d4f",
  }
})

export default CustomButton;
